mapper.update:
update sys_rorder SET rorder_type='1'  
where order_id = #{orderId} 
and menu_id = (select menu_id from sys_menu where menu_name=#{menuName})

update sys_tables set tables_type='0' where tables_id=#{0};
update sys_order set order_type='0' where order_id=#{1};
update sys_rorder set rorder_type='0' where order_id=#{1}
